package Basic_Java_Programming;

public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Executing the same statement again and again it is called looping statements
		// 1. For loop
		// 2. while loop
		// 3. do while loop
		// 4. for each loop
		
		// for loop
		
		/*
		 * Syntax-
				for(initialisation ; condition ; incre/dcre)
				{
					// our business code
					// body / statement…
				}
		 */
		
		for(int a=1; a<=10; a++)
		{
		System.out.println("something here…" + a);
		}
		System.out.println("Out of the loop");
		
		// write a script to print 10 numbers
		
		for(int i=1; i<11; i++) {
			System.out.println(i);
		}
		
		for(  ; ; ) {
			System.out.println("I am in infinite loopppp");
		}
		
		
		
		
		
		
	
		
	
	
	
	
	
	
	
	
}
}
